import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { IGetPicture } from './shared/interfaces';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  picturesList: IGetPicture[] = []

  constructor(private http: HttpClient) { }

  getPictures(picturePath: string) {
    return this.http.get<{ [picId: string]: IGetPicture }>(`https://my-project-angular-4dd57-default-rtdb.europe-west1.firebasedatabase.app/${picturePath}.json`);
  }

  getPicture(picId: string)  {
    return this.http.get<{ [picId: string]: IGetPicture }>(`https://my-project-angular-4dd57-default-rtdb.europe-west1.firebasedatabase.app/abstract/${picId}.json`)
    .pipe(map(resData => {
      const picturesArray = [];
      for (const key in resData) {
        if (resData.hasOwnProperty(key)) {
          picturesArray.push({ picId: key, ...resData[key] })
        }
      }
      return picturesArray
    }))
      .subscribe({
        next: (pictures) => {
          this.picturesList = pictures;
          console.log(this.picturesList);

        },
        error: (err) => {
          console.log(`Error: ${err}`);
        },
      }); 
  }
}
