import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-your-account',
  templateUrl: './your-account.component.html',
  styleUrls: ['./your-account.component.css']
})
export class YourAccountComponent {

  get isLoggedIn(): boolean {
    return this.authService.isLoggedIn;
  }

  constructor(private authService: AuthService) {}

}
<div class="mini-navbar-wrap">
    <nav>
        <ul>
            <li>
                <a routerLink="/auth/profile/account">Account</a>
            </li>
            <li>
                <a routerLink="/auth/profile/create-picture">Create Picture</a>
            </li>
        </ul>
    </nav>
</div>
