import { Component, OnInit } from '@angular/core';
import { map } from 'rxjs/operators';
import { ActivatedRoute } from '@angular/router';
import { ApiService } from 'src/app/api.service';
import { AuthService } from 'src/app/auth/auth.service';
import { IGetPicture } from 'src/app/shared/interfaces';

@Component({
  selector: 'app-abstract',
  templateUrl: './abstract.component.html',
  styleUrls: ['./abstract.component.css']
})
export class AbstractComponent implements OnInit {

  isLoading: boolean = true;
  picturesList: IGetPicture[] = []
  getPicture: [picId: string] | undefined;

  constructor(private apiService: ApiService,
    private authService: AuthService,
    private activatedRoute: ActivatedRoute) {
  }

  get isLoggedIn(): boolean {
    return this.authService.isLoggedIn;
  }

  ngOnInit(): void {
    this.apiService.getPictures('abstract').pipe(map(resData => {
      const picturesArray = [];
      for(const key in resData) {
        if(resData.hasOwnProperty(key)) {
          picturesArray.push({picId:key, ...resData[key]}) 
        }
      }
      return picturesArray
    }))
      .subscribe({
        next: (pictures) => {
          this.picturesList = pictures;
          console.log(this.picturesList);

          this.isLoading = false;
        },
        error: (err) => {
          this.isLoading = false;
          console.log(`Error: ${err}`);
        },
      });

      this.fetchPicture()
  }


  fetchPicture(): void {
    const id = this.activatedRoute.snapshot.params['picId'];
    console.log(this.activatedRoute.snapshot.params);


    this.apiService.getPictures(id).subscribe((picture) => {
      // this.getPicture = picture;
      console.log(`My id: ${picture}`);

    });
  }

}
