import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IGetUser } from 'src/app/shared/interfaces';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-user-details',
  templateUrl: './user-details.component.html',
  styleUrls: ['./user-details.component.css']
})
export class UserDetailsComponent implements OnInit {

  user: IGetUser | undefined;
  isLoading: boolean = true;

  constructor(private activatedRoute: ActivatedRoute, private authService: AuthService) {}

  ngOnInit(): void {
    const id: string = this.activatedRoute.snapshot.params['userId'];
    console.log(id);

    this.authService.getUser(id).subscribe({
      next: (user) => {
        this.user = user;
        console.log(user);
        this.isLoading = false;
      },
      error: (err) => {
        this.isLoading = false;
        console.log(`Error: ${err}`);
      },
    })
  }

}
<div class="container" *ngIf="isLoading">
    <app-loader />
    <div class="profile" >
        <h3>My Profile:</h3>
        <h2>Be Gallery</h2>
        <div>
            <h1>Be Gallery</h1>
            <h1>Be Gallery</h1>
            <h1>Be Gallery</h1>  
        </div>
        <ng-container *ngIf="!isLoading">
            <div class="field">
                <a [routerLink]="['/picture/abstract', user.userId]"></a>
                <p>Username:</p>
                <p>{{user!.username}}</p>
            </div>
            <div class="field">
                <p>Firstname:</p>
                <p>{{user!.firstname}}</p>
            </div>
            <div class="field">
                <p>Secondname:</p>
                <p>{{user!.secondname}}</p>
            </div>
            <div class="field">
                <p>Lastname:</p>
                <p>{{user!.lastname}}</p>
            </div>
            <div class="field">
                <p>Email: </p>
                <p>{{user!.email}}</p>
            </div>
            <div class="field">
                <p>Phone: </p>
                <p>{{user!.phone}}</p>
            </div>
            <div class="field">
                <p>Country:</p>
                <p>{{user!.country}}</p>
            </div>
            <div class="field">
                <p>Place:</p>
                <p>{{user!.place}}</p>
            </div>
            <div class="field">
                <p>Postcode:</p>
                <p>{{user!.postcode}}</p>
            </div>
            <div class="field">
                <p>Street:</p>
                <p>{{user!.street}}</p>
            </div>
        </ng-container>
