import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Router } from '@angular/router';
import { IGetUser, IUser, IUserLog } from 'src/app/shared/interfaces';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent implements OnInit {

  showEditMode: boolean = false;
  isLoading: boolean = true;
  userDetails: IUser | undefined;
  // userDetails: any;

  // userInfo: IUser | null = null

  userInfo: IUser = {
    username: 'ddd171717',
    firstname: 'Dilyan',
    secondname: 'Ivanov',
    lastname: 'Ivanov',
    email: 'dilyan@gmail.com',
    phone: '+3590888775555',
    country: 'Bulgaria',
    place: 'Yambol',
    postcode: '8600',
    street: 'Viza 6',
    password: '11111111',
    rePassword: '11111111'
  };


  form = this.fb.group({
    username: ['', [Validators.required, Validators.minLength(5)]],
    firstname: ['', [Validators.required, Validators.minLength(2)]],
    secondname: ['', [Validators.required, Validators.minLength(2)]],
    lastname: ['', [Validators.required, Validators.minLength(2)]],
    email: ['', [Validators.required]],
    phone: ['', [Validators.required, Validators.minLength(9)]],
    country: ['', [Validators.required, Validators.minLength(2)]],
    place: ['', [Validators.required, Validators.minLength(2)]],
    postcode: ['', [Validators.required, Validators.minLength(2)]],
    street: ['', [Validators.required, Validators.minLength(3)]],
  });


  constructor(private fb: FormBuilder, private router: Router,
  private authService: AuthService, private activatedRoute: ActivatedRoute) {
  }

  get isLoggedIn(): boolean {
    return this.authService.isLoggedIn;
  }



  ngOnInit(): void {

    const id: string = this.activatedRoute.snapshot.params['userId'];
    console.log(id);


    this.authService.getUser(id)
    .subscribe({
      next: (user) => {
        // console.log(user);

        this.userDetails = user;
        // console.log(this.userDetails);
        this.isLoading = false;
      },
      error: (err) => {
        this.isLoading = false;
        console.log(`Error: ${err}`);
      },
    })

    //   const id: string = this.activatedRoute.snapshot.params['userId'];
    // console.log(id);

    // this.authService.getUser(id)
    // .subscribe({
    //   next: (user) => {
    //     this.userDetails = user;
    //     console.log(this.userDetails);
    //     this.isLoading = false;
    //   },
    //   error: (err) => {
    //     this.isLoading = false;
    //     console.log(`Error: ${err}`);
    //   },
    // })

  }

  toggleEditMode(): void {
    this.showEditMode = !this.showEditMode;
  }

  saveAccount(): void {
    if (this.form.invalid) { return; }

    this.userInfo = { ...this.form.value } as IUser;

    this.toggleEditMode();
  }

  closeAccountForm(): void {
    this.router.navigate(["/auth/profile"]);
  }
}


