import { Component, OnInit } from '@angular/core';
import { map } from 'rxjs/operators';
import { ApiService } from 'src/app/api.service';
import { AuthService } from 'src/app/auth/auth.service';
import { IGetPicture } from 'src/app/shared/interfaces';

@Component({
  selector: 'app-animals',
  templateUrl: './animals.component.html',
  styleUrls: ['./animals.component.css']
})
export class AnimalsComponent implements OnInit {

  isLoading: boolean = true;
  picturesList: IGetPicture[] = []

  constructor(private apiService: ApiService, private authService: AuthService) {
  }

  get isLoggedIn(): boolean {
    return this.authService.isLoggedIn;
  }

  ngOnInit(): void {
    this.apiService.getPictures('animals').pipe(map(resData => {
      const picturesArray = [];
      for (const key in resData) {
        if (resData.hasOwnProperty(key)) {
          picturesArray.push({ picId: key, ...resData[key] })
        }
      }
      return picturesArray
    }))
      .subscribe({
        next: (pictures) => {
          this.picturesList = pictures;
          console.log(this.picturesList);

          this.isLoading = false;
        },
        error: (err) => {
          this.isLoading = false;
          console.log(`Error: ${err}`);
        },
      });
  }
}
<h2 class="title">Animals pictures</h2>
<div *ngIf="isLoading">
    <app-loader />
</div>
<div class="row" *ngIf="!isLoading">
    <div class="items" *ngFor="let pic of picturesList, let i = index">
        <img src={{pic.picImage}} alt="picture" class="picture">
        <div class="picture-name">
            <p>{{pic.picName}}</p>
        </div>
        <div class="picture-price">
            <p>{{pic.picPrice}}</p>
        </div>
    </div>
</div>
