import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { map } from 'rxjs/operators';
import { ApiService } from 'src/app/api.service';
import { AuthService } from 'src/app/auth/auth.service';
import { IGetPicture } from 'src/app/shared/interfaces';

@Component({
  selector: 'app-picture-details',
  templateUrl: './picture-details.component.html',
  styleUrls: ['./picture-details.component.css']
})
export class PictureDetailsComponent implements OnInit {

  onePicture: IGetPicture[] = []
  isLoading: boolean = true;

  constructor(private activatedRoute: ActivatedRoute, private apiService: ApiService,
    private authService: AuthService) {
  }

  get isLoggedIn(): boolean {
    return this.authService.isLoggedIn;
  }

  ngOnInit(): void {
    this.fetchPicture()

  }

  fetchPicture(): void {
    const id: string = this.activatedRoute.snapshot.params['picId'];

    console.log(id);


    this.apiService.getPicture(id)
    .pipe(map(resData => {
      const picturesArray = [];
      for (const key in resData){
        if (resData.hasOwnProperty(key)) {
          picturesArray.push({ picId: key, ...resData[key] })
        }
      }
      return picturesArray
    }))
    .subscribe({
      next: (picture) => {
        this.onePicture = picture;
        console.log(this.onePicture);

        this.isLoading = false;
      },
      error: (err) => { 
        this.isLoading = false;
        console.log(`Error: ${err}`);
      },
    });






    // this.apiService.getPictures(id).subscribe((picture) => {
    // this.getPicture = picture;
    // console.log(`My id: ${picture}`);
    // const pic = this.apiService.getPicture(id);
    // console.log(`This is pic${pic}`);


  }

}
<h2 class="title">Abstract pictures</h2>
<div *ngIf="isLoading">
    <app-loader />
</div>
<div class="row" *ngIf="!isLoading">
    <div class="items">
        <img alt="picture" class="picture">
        <div class="picture-name">
            <p></p>
        </div>
        <div class="picture-price">
            <p></p>
        </div>
    </div>
</div>
