import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { IUser, IUserLog } from '../../shared/interfaces';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  currentUser: string = ''

  usersList: IUser[] = [];
  user: IUserLog | undefined;
  USER_ = '[user]';

  get isLoggedIn(): boolean {
    return !!this.user;
  }

  constructor(private router: Router, private authService: AuthService,) {

  }

  ngOnInit(): void {

  }

  loginHandler(form: NgForm): void {
    if (form.invalid) { return; }
    // console.log(form.value.username);

    this.authService.checkUserExist()
      .subscribe({
        next: (users) => {
          this.usersList = users;
          console.log(this.usersList);
          const isUsernameCorrect = this.usersList.some(usernames =>
            usernames.username === form.value.username);

          if (isUsernameCorrect) {
            localStorage.setItem(this.USER_, JSON.stringify(form.value.username));
            const lsUser = localStorage.getItem(this.USER_) || '';
            this.user = JSON.parse(lsUser)
            console.log(this.user);

            this.router.navigate(["/"]);
          } else {
            return;
          }
        },
        error: (err) => {
          // this.isLoading = false;
          console.log(`Error: ${err}`);
        },
      });

    // this.isLoading = false;
  }

  logout(): void {
    this.user = undefined;
    localStorage.removeItem(this.USER_);
  }



  // getUser() {
  //   try {
  //     const lsUser = localStorage.getItem(this.USER_) || '';
  //     // console.log(lsUser);

  //     this.user = JSON.parse(lsUser);
  //     console.log(this.user);

  //   } catch (error) {
  //     this.user = undefined;
  //   }

  // }


  // isUserCorrect(form: NgForm) {
  //   return this.usersList.some(usernames =>
  //     usernames.username === form.value.username);
  //   if(isUsernameCorrect) {

  //     this.router.navigate(["/"]);
  //   }
  // }
}

<form #form="ngForm" class="login" (ngSubmit)="loginHandler(form)">
    <fieldset>
        <h2>Login Form</h2>

        <p class="field field-icon">
            <label for="username"><span><i class="fas fa-envelope"></i></span></label>
            <input #usernameInput="ngModel" class="input-error" ngModel required type="username"
                name="username" id="username" placeholder="username" [minlength]="5">
        </p>
        <ng-container *ngIf="usernameInput.touched">
            <p class="error" *ngIf="usernameInput.errors?.['required']">
                Username is required!
            </p>
            <p class="error" *ngIf="usernameInput.errors?.['minlength']">
                Username must be at least 5 characters!
            </p>
        </ng-container>

        <p class="field field-icon">
            <label for="password"><span><i class="fas fa-lock"></i></span></label>
            <input type="password" ngModel #passwordInput="ngModel" name="password" id="password" placeholder="password" required [minlength]="8">
        </p>
        <ng-container *ngIf="passwordInput.touched">
            <p class="error" *ngIf="passwordInput.errors?.['required']">
                Password is required!
            </p>
            <p class="error" *ngIf="passwordInput.errors?.['minlength']">
                Password must be at least 8 characters!
            </p>
        </ng-container>

        <button [disabled]="form.invalid">Login</button>

        <p class="text-center">
            Don't have an account yet?
            <a routerLink="/auth/register">Register</a>
        </p>
    </fieldset>
</form>
