import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { IGetUser } from 'src/app/shared/interfaces';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-user-details',
  templateUrl: './user-details.component.html',
  styleUrls: ['./user-details.component.css']
})
export class UserDetailsComponent implements OnInit {

  userList: IGetUser | undefined;
  isLoading: boolean = true;

  constructor(private activatedRoute: ActivatedRoute, private authService: AuthService) {}

  ngOnInit(): void {
    const id: string = this.activatedRoute.snapshot.params['userId'];
    console.log(id);

    this.authService.getUser(id)
    .subscribe({
      next: (user) => {
        this.userList = user;
        console.log(this.userList);
        // this.isLoading = false;
      },
      error: (err) => {
        // this.isLoading = false;
        console.log(`Error: ${err}`);
      },
    })
  }

}
<div *ngIf="!userList">
    <app-loader />
</div>
<div class="container">
    <div class="profile" *ngIf="userList">
        <h3>My Profile:</h3>
        <h2>Be Gallery</h2>
        <div>
            <h1>Be Gallery</h1>
            <h1>Be Gallery</h1>
            <h1>Be Gallery</h1>
        </div>
        <ng-container >
            <div class="field"> 
                <p>Username:</p>
                <p>{{userList.username}}</p>
            </div>
            <div class="field">
                <p>Firstname:</p>
                <p>{{userList.firstname}}</p>
            </div>
            <div class="field">
                <p>Secondname:</p>
                <p>{{userList.secondname}}</p>
            </div>
            <div class="field">
                <p>Lastname:</p>
                <p>{{userList.lastname}}</p>
            </div>
            <div class="field">
                <p>Email: </p>
                <p>{{userList.email}}</p>
            </div>
            <div class="field">
                <p>Phone: </p>
                <p>{{userList.phone}}</p>
            </div>
            <div class="field">
                <p>Country:</p>
                <p>{{userList.country}}</p>
            </div>
            <div class="field">
                <p>Place:</p>
                <p>{{userList.place}}</p>
            </div>
            <div class="field">
                <p>Postcode:</p>
                <p>{{userList.postcode}}</p>
            </div>
            <div class="field">
                <p>Street:</p>
                <p>{{userList.street}}</p>
            </div>
        </ng-container>
    </div>
</div>
