<h2 class="title">Children's pictures</h2>
<div *ngIf="isLoading">
    <app-loader />
</div>
<div class="row" *ngIf="!isLoading">
    <div class="items" *ngFor="let pic of picturesList, let i = index">
        <img src={{pic.picImage}} alt="picture" class="picture">
        <div class="picture-name">
            <p>{{pic.picName}}</p>
        </div>
        <div class="picture-price">
            <p>{{pic.picPrice}}</p>
        </div>
    </div>
</div>
