import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { PictureDetailsComponent } from '../picture-details/picture-details.component';
import { ApiService } from 'src/app/api.service';
import { IPicture } from 'src/app/shared/interfaces';

@Component({
  selector: 'app-edit-picture',
  templateUrl: './edit-picture.component.html',
  styleUrls: ['./edit-picture.component.css']
})
export class EditPictureComponent implements OnInit{

  // myPicture: any;

  myPicture: IPicture | undefined;

  constructor(private router: Router, private activatedRoute: ActivatedRoute,
    private pictureDetailsComponent: PictureDetailsComponent,
    private apiService: ApiService) {}

  // cancel(): void {
  //   this.router.navigate(["/auth/profile"]);

  // }

  ngOnInit(): void {

    const id: string = this.activatedRoute.snapshot.params['picId'];
    // this.myPicture = this.pictureDetailsComponent.myCurrPicture;
    // console.log(this.myPicture);


    const url = this.router.routerState.snapshot.url;
    let regExp = /(\/[a-zA-Z]*)(\/[a-zA-Z]*\/)([a-zA-Z]*)/gm
    const result: any = regExp.exec(url);

    const myId = '-NazG10UmADgX6d29RO2'

    this.apiService.getPictureToBeEdit('abstract', myId)
    .subscribe({
      next: (pic) => {
        this.picForEditForm = pic;
      },
      error: (err) => {
        console.log(`Error: ${err} `);
      }
    })


  }

}
